import React, { Component } from 'react'

class Jo extends Component {
  state={
    data:null
  }
  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      let confirmedJo = data.confirmed.value
      let recoveredJo = data.recovered.value;
      let deathsJo = data.deaths.value;
      let jo = [confirmedJo, recoveredJo, deathsJo];
      console.log("data", confirmedJo);

      this.setState({
        data:jo
      })
      // console.log(data);
    });

  }
  render() {
    // let som = this.state.data

    // console.log("som", som);

    return (
      <div>
        <h1>Jordan Component</h1>
        <li>{this.state.data}</li>
      </div>
    )
  }
}

export default Jo;




