import React, { Component } from 'react'

class Input extends Component {
  state={
    data:"Mansour"
  }
  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      // let data = data.confirmed
      let confirmedJo = data.confirmed.value

      console.log(data);
      this.setState({
        data:confirmedJo
      })
    })

  }
  render() {
    let som = this.state.data
    return (
      <div>
        <h1>Input Component</h1>
        <li>{som}</li>
      </div>
    )
  }
}

export default Input;
