import React, { Component } from 'react'

class Global extends Component {
  state = {
    data: null,
    summary: null
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api/countries/[country]/confirmed")
    .then(response => response.json())
    .then(data => {
      let entireData = data.map((el, index) => {
        return (
          <div key={Math.random() * 30}>            
            <li style={{"width":"700px"}}><strong >Country:</strong>  {el.countryRegion} <span style={{"color":"orange"}}><strong>Confirmed:</strong> {el.confirmed}</span> __<span style={{"color":"red"}}><strong>Deaths:</strong> {el.deaths}</span></li>
          </div>
        )
      })
      this.setState({data: entireData})
      // console.log(data);
      console.log("componentDID");
    });
  }
  render() {
    return (
      <div>
        <p>Global Component</p>
      </div>
    )
  }
}

export default Global;
