import React, { Component } from 'react'

class Jo extends Component {
  state={
    data:null
  }
  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      // let data = data.confirmed
      let confirmedJo = data.confirmed.value;
      let recoveredJo = data.recovered.value;
      let deathsJo = data.deaths.value;
      let jo = [confirmedJo, recoveredJo, deathsJo];

      console.log(data);
      this.setState({
        data:jo
      })
    })

  }
  render() {
    let som = this.state.data
    return (
      <div>
        <h1>Input Component</h1>
        <li>{som.confirmedJo}</li>
      </div>
    )
  }
}

export default Jo;
