import React, { Component } from 'react';
import './App.css';
import Global from "./Global";
import Jo from "./Jo";
import Kw from "./Kw";

class App extends Component {

  state = {
    data: null
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api/countries/[country]/confirmed")
    .then(response => response.json())
    .then(data => {
      console.log("App", data);

      let entireData = data.map((el, index) => {
        if(el.provinceState !== null ) {
        return (
          <div className="entireData-items" key={index}>            
              <h3><strong>{el.countryRegion}, {el.provinceState}:</strong></h3>
              <li className="item" style={{"color":"orange", "background": "#ffdfb5"}}><label>مؤكد:</label> {el.confirmed}</li>
              <li className="item" style={{"color":"green", "background":"#e2fdcc"}}><label> متعافي:</label> {el.recovered}</li>
              <li className="item" style={{"color":"red", "background":"#fbe3e3"}}><label> وفاة:</label> {el.deaths}</li>
          </div>
        )
        } else {
          return (
            <div className="entireData-items" key={index}>            
                <h3><strong>{el.countryRegion}</strong></h3>
                <div className="global-item">
          <p style={{"color": "orange"}}> {el.confirmed} </p>
          <label>مؤكد</label>
        </div>

        <div className="global-item">
          <p style={{"color": "green"}}> {el.recovered}</p>
          <label>متعافي</label>
        </div>

        <div className="global-item">
          <p style={{"color": "red"}}> {el.deaths}</p>
          <label>وفاة</label>
        </div>
            </div>
          )
        }
    })
      this.setState({data: entireData})
      // console.log(data);
    });
  }








  render() {
    return (
      <div className="app">
        <div className="container">
          <h1>Covid-19  --  كورونا</h1>
          <h5 style={{"fontSize": "8px", "textAlign":"center"}}>Made by <a href="http://codings.io">codings.io</a></h5>
          <Global/>
          <Jo/>
          <Kw/>
          <h1>تفاصيل كل دولة</h1>
          <div className="dataContainer" >{this.state.data}</div>
          <h5 style={{"fontSize": "12px", "textAlign":"center"}}>Made by <a href="http://codings.io">codings.io</a>@ 2020</h5>

        </div>
      </div>

    );
  }
}

export default App;



