import React, { Component } from 'react';
import './App.css';
import Global from "./Global";
import Jo from "./Jo";
import Kw from "./Kw";

class App extends Component {

  state = {
    data: null
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api/countries/[country]/confirmed")
    .then(response => response.json())
    .then(data => {
      console.log("App", data);

      let entireData = data.map((el, index) => {
        if(el.provinceState !== null ) {
        return (
          <div className="entireData-items" key={index}>            
              <h3><strong>Country: {el.countryRegion}, State: {el.provinceState}:</strong></h3>
              <li className="item" style={{"color":"orange"}}><strong>مؤكد:</strong> {el.confirmed}</li>
              <li className="item" style={{"color":"green"}}><strong>حالة معافاة:</strong> {el.recovered}</li>
              <li className="item" style={{"color":"red"}}><strong>حالات وفاة:</strong> {el.deaths}</li>
          </div>
        )
        } else {
          return (
            <div className="entireData-items" key={index}>            
                <h3><strong>Country: {el.countryRegion}</strong></h3>
                <li className="item" style={{"color":"orange"}}><strong>مؤكد:</strong> {el.confirmed}</li>
                <li className="item" style={{"color":"green"}}><strong>حالة معافاة:</strong> {el.recovered}</li>
                <li className="item" style={{"color":"red"}}><strong>Deaths:</strong> {el.deaths}</li>
            </div>
          )
        }
    })
      this.setState({data: entireData})
      // console.log(data);
    });
  }

  render() {
    return (
      <div className="container">
        <h1>Covid-19</h1>
        <Global/>
        <Jo/>
        <Kw/>
        <h1>All The World</h1>
        <div className="dataContainer" >{this.state.data}</div>
      </div>
    );
  }
}

export default App;



