import React, { Component } from 'react'

class Jo extends Component {
  state={
    jo:[]
  }

  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      let confirmedJo = data.confirmed.value
      let recoveredJo = data.recovered.value;
      let deathsJo = data.deaths.value;
      let jo = [confirmedJo, recoveredJo, deathsJo];

      this.setState({
        jo
      })
    });
  }
  render() {
    return (
      <div>
        <h1>Jordan Component</h1>
        {console.log(this.state)}
        <li>{this.state.jo}</li>
      </div>
    )
  }
}

export default Jo;


