import React, { Component } from 'react'

class Jo extends Component {
  state={
    jo:[]
  }
  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      let confirmedJo = data.confirmed.value
      let recoveredJo = data.recovered.value;
      let deathsJo = data.deaths.value;
      let jo = [confirmedJo, recoveredJo, deathsJo];
      console.log("data", confirmedJo);
      console.log("joooooo", jo);

      this.setState({
        data : jo
      })
      console.log("DIDNOUNT", this.state);

      // console.log(data);
    });

  }
  render() {
    // let {e1,e2,e3} = this.state.data;
    console.log("render", this.state);


    return (
      <div>
        <h1>Jordan Component</h1>
        {/* <li>{elem.confirmedJo}</li> */}
      </div>
    )
  }
}

