import React, { Component } from 'react'

class Jo extends Component {
  state={
    data:null
  }
  componentDidMount() {
    fetch("https://covid19.mathdro.id/api/countries/Jordan")
    .then(res => res.json())
    .then(data => {
      let joData = data.map((el, index) => {
        return (
          <div className="entireData-items" key={index}>            
              <h3><strong>{el.countryRegion}:</strong></h3>
              <li className="item" style={{"color":"orange"}}><strong>Confirmed:</strong> {el.confirmed}</li>
              <li className="item" style={{"color":"green"}}><strong>Recovered:</strong> {el.recovered}</li>
              <li className="item" style={{"color":"red"}}><strong>Deaths:</strong> {el.deaths}</li>
          </div>
        )
      })
      this.setState({data: joData})
      // console.log(data);
    });

  }
  render() {
    // let som = this.state.data

    // console.log("som", som);

    return (
      <div>
        <h1>Jordan Component</h1>
        <li>{this.state.data}</li>
      </div>
    )
  }
}

export default Jo;




