import React, { Component } from 'react'
import './App.css';

class Global extends Component {
  state = {
    global: "mansour"
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api")
    .then(response => response.json())
    .then(data => {
      console.log("GLOBAL", data);

      let globalConfirmed = data.confirmed.value;
      let globalRecovered = data.recovered.value;
      let globalDeaths = data.deaths.value;
      let global = [globalConfirmed, globalRecovered, globalDeaths];
      this.setState({ global:global })
    });
  }

  render() {
    let som = this.state

    return (
      <div className="entireData-items">
        <h2>مجموع الحالات حول العالم </h2>
        <span style={{"color": "orange", "background": "#ffdfb5"}}> <label>مؤكد</label>: {som.global[0]} </span>

        <span style={{"color": "green", "background":"#e2fdcc"}}>متعافي : {som.global[1]}</span>
        <span style={{"color": "red", "background":"#fbe3e3"}}> وفاة: {som.global[2]}</span>
        <br></br>
        <br></br>

      </div>
    )
  }
}

export default Global;
