import React, { Component } from 'react'

class Global extends Component {
  state = {
    global: null
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api")
    .then(response => response.json())
    .then(data => {
      let globalConfirmed = data.confirmed.value;
      let globalRecovered = data.recovered.value;
      let globalDeaths = data.deaths.value;
      let global = [globalConfirmed, globalRecovered, globalDeaths];
      this.setState({ global:global })

      console.log("GLOBAL", global);
    });
  }

  render() {
    // let {globalConfirmed, globalRecovered, globalDeaths} = this.state;
    // let global = this.state
    let maped = this.state.global.map(el => {
      return (
      <div>
        <li>{el.globalConfirmed}</li>
        <li>{el.globalRecovered}</li>
        <li>{el.globalDeaths}</li>
      </div>
      )
    })

    console.log("render", global);

    return (
      <div>
        <p>Global Component</p>
        {maped}
      </div>
    )
  }
}

export default Global;
