import React, { Component } from 'react'

class Global extends Component {
  state = {
    global: "mansour"
  };

  componentDidMount(){
    fetch("https://covid19.mathdro.id/api")
    .then(response => response.json())
    .then(data => {
      console.log("GLOBAL", data);

      let globalConfirmed = data.confirmed.value;
      let globalRecovered = data.recovered.value;
      let globalDeaths = data.deaths.value;
      let global = [globalConfirmed, globalRecovered, globalDeaths];
      this.setState({ global:global })
    });
  }

  render() {
    let som = this.state

    return (
      <div>
        <h1>Global </h1>
        <h3 style={{"color": "orange"}}>Confirmed: {som.global[0]}</h3>
        <li style={{"color": "green"}}>Recovered: {som.global[1]}</li>
        <li style={{"color": "red"}}>Deaths: {som.global[2]}</li>
        <br></br>
        <br></br>

      </div>
    )
  }
}

export default Global;
